import React from 'react';

import InputForm from './inputForm';
import OutputView from './outputView';

class MicronAutApp extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = { 
      name: '',
      isSubmitting: false,
      submitError: '',
    }

    this.onChangeName = this.onChangeName.bind(this);
    this.submitName = this.submitName.bind(this);
  }

  onChangeName(e) {
    const name = e.target.value;
    this.setState({
      name,
    })
  }

  submitName() {
    try {
      const { name } = this.state;
      this.setState({
        isSubmitting: true
      })
      fetch(`http://0.0.0.0:8080/welcome/${name}`, {
            method: 'get',
            headers: { 'Content-Type': 'text/plain' },
        }).then((response) => response.json())
        .then((responseJson) => {
            console.log('Res', responseJson);
            this.setState({ calculatedData: responseJson, isWorking: false });
        })
        .catch((error) => {
            this.setState({ submitError: 'Unable to complete the submission.'})
            console.log('Error', error)
        });
    } catch(e) {

    }
  }

  render() {
    return (
      <div className="wrapper">
        <div className="title">
          <h2>Micronaut Demo App</h2>
        </div>
        <div className="input-area">
          <label> Enter Your Name </label>
          <input onChange={this.onChangeName} />
          <button onClick={this.submitName}>Submit</button>
        </div>
      </div>
    )
  }
}

export default React.memo(MicronAutApp);
